<template>
  <div id="app">
    <button @click="incrementCounter">Pencet disini bro</button>
    <div class="box" :style="{ backgroundColor: boxColor }"></div>
    <p>hitungan kamu klik: {{ counter }}</p>
    
    <div class="carousel">
      <button @click="prevImage">Previous</button>
      <img :src="currentImage" alt="carousel image" />
      <button @click="nextImage">Next</button>
    </div>
  </div>
</template>

<script setup>
import { ref, computed } from 'vue';

const counter = ref(0);
const colors = ['#FF5733', '#33FF57', '#3357FF', '#F0F0F0'];
const currentColorIndex = ref(0);
const boxColor = computed(() => colors[currentColorIndex.value]);

function incrementCounter() {
  counter.value += 1;
  currentColorIndex.value = (currentColorIndex.value + 1) % colors.length;
}

const images = [
  'data:image/jpeg;base64,',
  'data:image/jpeg;base64,',
  'data:image/jpeg;base64,'
];
const currentIndex = ref(0);

const currentImage = computed(() => images[currentIndex.value]);

function prevImage() {
  currentIndex.value = (currentIndex.value - 1 + images.length) % images.length;
}

function nextImage() {
  currentIndex.value = (currentIndex.value + 1) % images.length;
}
</script>

<style>
.box {
  padding: 15px 32px;
  text-align: center;
  text-decoration: none;
  display: inline-block;
  font-size: 16px;
}

.carousel {
  margin-top: 20px;
  text-align: center;
}

.carousel img {
  margin: auto;
  width: 100%;
  border: 3px solid green;
  padding: 10px;
}
</style>
